function scrollFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?document.getElementById("myBtn").style.display="block":document.getElementById("myBtn").style.display="none"}$(document).ready(function(){$("a").on("click",function(n){if(""!==this.hash){n.preventDefault();var o=this.hash;$("html, body").animate({scrollTop:$(o).offset().top},1e3,function(){window.location.hash=o})}})}),window.onscroll=function(){scrollFunction()},$("#myBtn").each(function(){$(this).click(function(){return $("html,body").animate({scrollTop:0},"slow"),!1})});